sp_widget.client_script:
api.controller=function($rootScope) {
  /* widget controller */
  var c = this;

	c.selected_bank_id = false;

	c.fn = {
		pickQuestionBank: function(bank_id) {
			if (c.selected_bank_id == bank_id)
				return;

			c.selected_bank_id = bank_id;
			$rootScope.$broadcast("coolreview.bank.picked", bank_id)
		}
	};

};

sp_widget.css:
tr td a {
  cursor: pointer;
  width: 100%;
  height: 100%;
  padding: 15px 10px;
  display: block;
}

tr, tr td {
  padding: 0;
}

tr td a:hover {
  background-color: #d9edff;
}

tr td a.selected {
  background-color: #d9daff;
  font-weight: bold;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.rows = [];

	if (!input) {
		var gr = new GlideRecord("x_650610_c_review_question_bank");
		gr.orderBy("order");
		gr.orderBy("sys_created_on");
		gr.query();
		while (gr.next()) {
			var row = {};
			row.sys_id = gr.getUniqueValue();
			row.name = String(gr.name);
			row.description = String(gr.description);
			row.order = String(gr.order);
			data.rows.push(row);
		}
	}



})();
