sys_script_include.script:
var QuestionBank = Class.create();
QuestionBank.prototype = {
    initialize: function() {
    },

    type: 'QuestionBank'
};

(function(self) {

	self.TABLENAME = "x_650610_c_review_question_bank";
	self.ObjectFields = [ "name", "description", "order" ];


	self.Glide = function(question_bank_id) {
		var gr = new GlideRecord(self.TABLENAME);
		if (!gr.get(question_bank_id)) {
			return false;
		}

		return gr;
	};

	self.GetObject = function(question_bank_id) {
		var gr_bank = question_bank_id;
		if (typeof question_bank_id == "string") {
			gr_bank = self.Glide(question_bank_id);
		}
		if (typeof gr_bank == "object" && !Array.isArray(gr_bank)) {
			var result = {};
			for (var i in self.ObjectFields) {
				var field = self.ObjectFields[i];
				result[field] = {
					value: String(gr_bank[field]),
					display_value: gr_bank[field].getDisplayValue()
				};
			}

			return result;
		}


		return false;
	};


})(QuestionBank);
